import React from 'react';
import './ProductCard.css';
import {FaCartPlus} from 'react-icons/fa';
import propTypes from 'prop-types';

function ProductCard({ data }){

  const {title, thumbnail, price} = data;
  const thumbnail2 = thumbnail.replace(/\w\.jpg/gi, 'W.jpg'); /*Substituindo caractre na url que define qualidade da imagem pela melhor, caractere W. Leia-se: Qualquer caractere.jpg será substituído por W.jpg. */

  return(
    <section className='product-card'>

      <img 
        src={thumbnail2}
        alt='product' 
        className='card__image'
      />

      <div className='card__infos'>
        <h2 className='card__price'>{price.toLocaleString}</h2>
        <h2 className='card__title'>{title}</h2>
      </div>

      <button type='button' className='button__add-cart'>
        <FaCartPlus />
      </button>
    </section>
  );
}

export default ProductCard;

ProductCard.propTypes = {
  data: propTypes.shape({}),
}.isRequired;
